import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {useState} from 'react';
import {useForm} from 'react-hook-form';
import './add-form.css';

function Ingredient(props){
    return
    <option value={props.name}>{props.name}</option>
  }

  function CategoryType(props){
    return
        <option value={props.name}>{props.name}</option>
  }

  function IngredientList(){
    const [selectedIngredient, setSelectedIngredient] = useState('');
    const [selectedUnit, setSelectedUnit] = useState('');
    const [addedItems, setAddedItems] = useState([]);

    const handleAddClick = () => {
        if(selectedIngredient && selectedUnit){
            const newItem = `${selectedIngredient} - ${selectedUnit}`;
            setAddedItems([...addedItems, newItem]);
            setSelectedIngredient('');
            setSelectedUnit('');
        }
    }
  };
  const renderAddedItems = () =>{
    return(
        <ul>
            {addedItems.map((item, index) => (
                <li key={index}>{item}</li>
            ))}
        </ul>
    );
  };
  const handleIngredientChange = (e) =>{
    setSelectedIngredient(e.target.value);
  };
  const handleUnitChange = (e) =>{
    setSelectedUnit(e.target.value);
  };



export default function AddForm(){
    const {register, handleSubmit, formState: {errors}} = useForm();
    const onSubmit = data => console.log(data);
    console.log(errors);
    // const ingredientsList = `api/ingredient/:id;
    // const categoryType = `api/category/:id`;
    function IngredientList(){
        const [selectedIngredient, setSelectedIngredient] = useState('');
        const [selectedUnit, setSelectedUnit] = useState('');
        const [addedItems, setAddedItems] = useState([]);
    
        const handleAddClick = () => {
            if(selectedIngredient && selectedUnit){
                const newItem = `${selectedIngredient} - ${selectedUnit}`;
                setAddedItems([...addedItems, newItem]);
                setSelectedIngredient('');
                setSelectedUnit('');
            }
        }
      };
      const renderAddedItems = () =>{
        return(
            <ul>
                {addedItems.map((item, index) => (
                    <li key={index}>{item}</li>
                ))}
            </ul>
        );
      };
      const handleIngredientChange = (e) =>{
        setSelectedIngredient(e.target.value);
      };
      const handleUnitChange = (e) =>{
        setSelectedUnit(e.target.value);
      };

  return (
    <div className='add-form'>
        <h2 id='form-title'>Add a new recipe</h2>

        <form onSubmit={handleSubmit(onSubmit)}>
            {/* titlu */}
            <label htmlFor="title">Recipe title</label>
            <input id='title' className='form-item' type="text" placeholder='Recipe title' {...register('Recipe title', {required:true})} />

            {/* img link */}
            <label htmlFor="img-link">Image link</label>
            <input id='img-link' className='form-item' type="url" placeholder='Image link' {...register('Image link', {required:true})} />

            {/* ingredient list  */}
            <label htmlFor="ingredient-name">Ingredients</label>
            <select id='ingredient-name' value={selectedIngredient} onChange={handleIngredientChange} className='form-item' {...register('Ingredient', {required:true})}>
                {/* {ingredientsList.map((ingredient) => <Ingredient name={ingredientsList}/>)} */}
            </select>

              {/* measure unit, functie sa leg un ingredient de cantitatea lui */}
              <label htmlFor="measure-unit">Measure unit</label>
              <select id='measure-unit' value={selectedUnit} onChange={handleUnitChange} className='form-item' {...register('Measure unit', {required:true})}>
                <option value="grams">grams</option>
                <option value="mililiters">mililiters</option>
                <option value="teaspoon">teaspoon</option>
                <option value="tablespoon">tablespoon</option>
                <option value="cup">cup</option>
                <option value="fl oz">fl oz</option>
                <option value="gallon">gallon</option>
                <option value="oz">oz</option>
              </select>  

              {/* buton sa se formeze o lista bu ingredientele si masura fiecaruia */}
              <button onClick={handleAddClick} >add</button>

            {/* steps  */}
            <label htmlFor="steps">Steps</label>
            <textarea id='steps' className='form-item' {...register('Steps', {required:true, maxLenght:200})}></textarea>

            {/* total duration, dupa : pun const-ul care aduna toate inputurile de duration de mai sus*/}
            <label htmlFor="duration">Total duration: </label>
            <input id='duration' className='form-item' type="number" placeholder='duration (minutes)' {...register('Duration (minutes)', {required:true})} />

            {/* complexity level */}
            <label htmlFor="complexity">Complexity</label>
            <select id='complexity' className='form-item' {...register('Complexity', {required:true})}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            
            {/* category type */}
            <label htmlFor="category-type">Category</label>
            <select id="category-type" className='form-item' {...register('Category', {required:true})}>
                {/* {categoryType.map((category) => <CategoryType name={categoryType}/>)} */}
            </select>

            {/* button de add reteta, functie care sa adauge reteta la lista de retetete */}
            <button id='add-btn' type='submit'>Add Recipe</button>
        </form>
    </div>
  )
}
