import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './sign-in.css'

//find out cum sa fac sa tin minte in acel obiect si token si sa salvez toate astea in Useri
//de pus o functie pe butonu de submit ca sa intri in cont (sa se inregistreze tokenu)

export default function SignIn(){
  
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  function handleLogin(){
    const auth = {
      username: username,
      password: password
    };
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(auth)
    };
    fetch('https://api-recipes.luanabuca.com/auth/login', options)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        if(data.error===1){
          alert ('Wrong username or password');
        }else{
          alert('You are in');
        }
      })
      .catch(error => console.error(error));
  }

  //validation
  // const signIn = document.getElementsByClassName('sign-in');

  // function validateSignIn(event){
  //   event.preventDefault();

  //   const username = document.getElementById('username');
  //   const password = document.getElementById('password');

  //   if(!validateUsername(username.value)){
  //     alert('You have entered the wrong username');
  //     return;
  //   }

  //   if(!validatePassword(password.value)){
  //     alert('You have entered the wrong password');
  //     return;
  //   }

  //   alert('Login successful!');
  // }

  // function validateUsername(username){
  //   const usernameRegex = "^[A-Za-z]\\w{5, 29}$"   //incepe cu o litera, accepta underscore si poate sa fie intre 6 si 30 char
  //   return usernameRegex.test(username);
  // }

  // function validatePassword(){
  //   const passwordRegex = '^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$'   //min 8 char, macar o litera si un nr
  //   return passwordRegex.test(password);
  // }

  return (
    <div className='sign-in' onSubmit={validateSignIn}>
      <h1 id='sign-in-title'>Sign In</h1>

      <div>
        <label htmlFor="username">Username: </label>
        <input type="text" id='username' className='input-item' onChange={(e) => {setUsername(e.target.value)}} placeholder='Username' />
      </div>

      <div>
        <label htmlFor="password">Password: </label>
        <input type="password" id='password' className='input-item' onChange={(e) => {setPassword(e.target.value)}} placeholder='Password' />
      </div>

      <div>
        <input type="button" id='sign-in-btn' onClick={handleLogin} value={'Login'} />
      </div>

      <p>
        <Link to='../sign-up'>Don't have an account yet? Click here to create one!</Link>
      </p>
    </div>
  )
}
