import React from 'react';
import {useState} from 'react';
// import {Listbox} from '@headlessui/react';
import {useForm} from 'react-hook-form';
import './add-form.css';

export default function AddForm(){
    const {register, handleSubmit, formState: {errors}} = useForm();
    const onSubmit = data => console.log(data);
    console.log(errors);
    const ingredientsList = `api/ingredient/:id`;

  return (
    <div>
        <form onSubmit={handleSubmit(onSubmit)}>
            {/* titlu */}
            <label htmlFor="title">Recipe title</label>
            <input id='title' type="text" placeholder='Recipe title' {...register('Recipe title', {required:true})} />
            {/* img link */}
            <label htmlFor="img-link">Image link</label>
            <input id='img-link' type="url" placeholder='Image link' {...register('Image link', {required:true})} />
            {/* ingredient list  */}
            
              {/* measure unit  */}
              <label htmlFor="measure-unit"></label>
              <select id='measure-unit' {...register('Measure unit', {required:true})}>
                <option value="grams">grams</option>
                <option value="mililiters">mililiters</option>
                <option value="teaspoon">teaspoon</option>
                <option value="tablespoon">tablespoon</option>
                <option value="cup">cup</option>
                <option value="fl oz">fl oz</option>
                <option value="gallon">gallon</option>
                <option value="oz">oz</option>
              </select>
              {/* button add la lista de ingrediente, pun functie sa se adauge la reteta */}
              <button>add</button>   
            {/* steps  */}
            <label htmlFor="steps">Steps</label>
            <textarea id='steps' {...register('Steps', {required:true, maxLenght:200})}></textarea>
              {/* duration, pun functie care sa adune toate inputurile */}
              <label htmlFor="duration">Duration-minutes</label>
              <input id='duration' type="text" placeholder='Duration' {...register('Duration', {required:true})} />
              {/* buton add la fiecare step, pun functie sa se adauge la reteta */}
              <button>add</button>
            {/* total duration, dupa : pun const-ul care aduna toate inputurile de duration de mai sus*/}
            <p>Total duration:  </p>
            {/* complexity level */}
            <label htmlFor="complexity">Complexity</label>
            <select id='complexity' {...register('Complexity', {required:true})}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
            </select>
            {/* category  */}
            {/* button de add reteta, functie care sa adauge reteta la lista de retetete */}
            <button type='submit'>Add Recipe</button>
        </form>
    </div>
  )
}
