import React, {useState, useEffect} from "react"; 
import './edit-form.css';

export default function EditForm(){
    if(localStorage.getItem('token')==null){
        window.location.replace('/sign-in');
    }

    //get categories
    const [categories, setCategories] = useState([]);
    const optionsGet = {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        }
    };

    useEffect(() => {
        fetch('https://api-recipes.luanabuca.com/categories/search', optionsGet)
         .then(response => response.json())
         .then(data => {
            setCategories(data['payload']['categories']);
         })
         .catch(error => console.error(error));
    }, []);

    const queryParameters = new URLSearchParams(window.location.search)
    const recipeId = queryParameters.get('recipeId')

    const recipeUrl = 'https://api-recipes.luanabuca.com/recipes/'+recipeId;

  const [recipe, setRecipe] = useState([]);

  const options = {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'jwt' : localStorage.getItem('token')
    }
  };

  useEffect(() => {
    fetch(recipeUrl, options)
     .then(response => response.json())
     .then(data => {
      // console.log(data['payload']['recipe']);
      setRecipe(data['payload']['recipe']);
     })
     .catch(error => console.error(error));
  }, [])

    return(
        <div className="edit-form">
            <h2 id="edit-title">Edit this recipe</h2>

            <div className="form">

                <label htmlFor="title">Recipe title</label>
                <input type="text" id="title" className="form-item" placeholder="Recipe title" value={recipe.title}/>

                <label htmlFor="description">General description</label>
                <p className='parag'><small>Here you will have to type a few words about your recipe, it's ingredients, and steps of your delicious recipe.</small></p>
                <textarea id="description" className="form-item" >{recipe.description}</textarea>

                <label htmlFor="img-link">Image link</label>
                <input type="url" id="img-link" className="form-item" placeholder="Image link" value={recipe.image}/>

                <label htmlFor="category-type">Category</label>
                <select id="category-type" className="form-item" defaultValue={recipe.category.id}>
                    {categories.map(category => (
                        <option key={category.id} value={category.id}>{category.name}</option>
                    ))}
                </select>

                <label htmlFor="complexity">Complexity</label>
                <select id="complexity" className="form-item" defaultValue={recipe.complexity}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>

                <button id="edit-form-btn" type="submit">Update Recipe</button>
            </div>
        </div>
    )
}
