import './App.css'
import Navbar from './navbar/Navbar.jsx';
import HomePage from './home-page/HomePage.jsx';
import RecipePage from './recipe-page/RecipePage';
import AddForm from './add-from/AddForm';
import Auth from './auth/Auth';

function App() {
  let Component 

  switch(window.location.pathname){
    case '/':
      Component = HomePage
      break
    case '/category':
      Component = HomePage
      break
    case '/my-account':
      Component = Auth
      break
    case 'recipe-page':
      Component = RecipePage
      break
    case '/my-account/add-form':
      Component = AddForm
      break
    case 'logout':
      localStorage.removeItem('token');
      Component = HomePage
      window.location.replace('/');
      break
  }

  return(
    <>
    <Navbar/>
    <section>
      <Component></Component>
    </section>
    </>
  )
}

export default App
