import './App.css';
import HomePage from './home-page/HomePage';
import Navbar from './navbar/Navbar';
import RecipeCategory from './category/RecipeCategory';
import RecipePage from './recipe-page/RecipePage';
import SignIn from './sign-in/SignIn';
import SignUp from './sign-up/SignUp';
import MyAccount from './my-account/MyAccount';
import AdminPage from './admin-page/AdminPage';
import FavoritesPage from './favorites/FavoritesPage';
import AddForm from './add-from/AddForm';
import RecipeItem from './recipe-item/RecipeItem';

import React from 'react';
import {Route, Routes, BrowserRouter as Router, BrowserRouter} from 'react-router-dom';
import UserContextProvider from './UserContext';




export default function App() {

  let Component
   switch(window.location.pathname){
    case '/':
      Component = HomePage
      break
    case '/category':
      Component = HomePage
      break
    case 'my-account':
      Component = SignIn
      break
    case '/recipe-page':
      Component = RecipePage
      break
    case '/my-account/add-form':
      Component = AddForm
      break
    case '/logout':
      localStorage.removeItem('token');
      Component = HomePage
      window.location.replace('/');
      break
   }

  return (
    // <>
    //     <Navbar></Navbar>
    //     <section>
    //       <Component></Component>
    //     </section>
    // </>

    <UserContextProvider>
    <BrowserRouter>
          <Navbar />

          <Routes>
           <Route path='/' exact element={<HomePage />} />
           <Route path='/category' element={<RecipeCategory/>} />
           <Route path='/recipe-page' element={<RecipePage/>} />
           <Route path='/sign-in' element={<SignIn/>} />
           <Route path='/sign-up' element={<SignUp/>} />
           <Route path='/my-account' element={<MyAccount/>} />
           <Route path='/recipe-item' element={<RecipeItem/>}/>
           <Route path='/admin-page' element={<AdminPage/>} />
           <Route path='/favorites' element={<FavoritesPage/>} />
           <Route path='/add-form' element={<AddForm/>} />
          </Routes>
      </BrowserRouter>
      </UserContextProvider>
  );
}
