import { useEffect, useState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

export default function Register(){
    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [passwordError, setPasswordError] = useState(null);

    function register(event){
        event.preventDefault();
        const body = {
            name,
            email,
            username,
            password,
        };

        //aici bag fetch
    }

    useEffect(() => {
        function validatePassword(password){
            const specialChar = ['*', '#', '@'];

            if(password <= 8){
                setPasswordError('Password must be at least 8 characters.');
                return;
            }

            let hasSpecialChar = false;

            for(let character of password){
                const hasChar = specialChar.includes(character);

                if(hasChar){
                    hasSpecialChar = true;
                }
            }

            if(!hasSpecialChar){
                setPasswordError('Your password must have at least one special character.', specialChar)
            }else{
                setPasswordError(null);
            }
        }

        validatePassword(password);
    }, [password]);

    return(
        <Form style={{maxWidth: '800px', margin: '0 auto'}} onSubmit={register}>
            <Form.Group className='mb-3' controlId='formBasicName'>
                <Form.Label>Name </Form.Label>
                <Form.Control 
                  type= 'text'
                  placeholder= 'Enter your Name'
                  value= {name}
                  onChange={(event) => setName(event.target.value)}
                />
            </Form.Group>

            <Form.Group className='mb-3' controlId='formBasicUsername'>
                <Form.Label>Username </Form.Label>
                <Form.Control 
                  type= 'text'
                  placeholder= 'Enter your username'
                  value= {username}
                  onChange={(event) => setUsername(event.target.value)}
                />
            </Form.Group>

            <Form.Group className='mb-3' controlId='formBasicEmail'>
                <Form.Label>Email address </Form.Label>
                <Form.Control 
                  type='email'
                  placeholder='Enter email'
                  value={email}
                  onChange={(event) => setEmail(event.target.value)}
                />
                <Form.Text>We'll never share your email with anyone.</Form.Text>
            </Form.Group>

            <Form.Group className='mb-3' controlId='formBasicPassword'>
                <Form.Label>Password </Form.Label>
                <Form.Control 
                  type='password'
                  placeholder= 'Password'
                  value= {password}
                  onChange={(event) => setPassword(event.target.value)}
                />
            </Form.Group>

            {passwordError && (
                <Form.Text>{passwordError}</Form.Text>
            )}

            <Button type='submit'>Create account</Button>
        </Form>
    );
}
