import React from 'react';
import {useForm} from 'react-hook-form';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import './sign-up.css'

//find out cum sa fac sa tin minte in acel obiect si token si sa salvez toate astea in Useri
//de pus o functie de inregistrat datele pe butonu de submit?

export default function SignUp(){
  const {register, handleSubmit, formState: {errors}} = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);

  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  function handleCreateAcc(){
    const auth ={
      name: name,
      username: username,
      password: password
    };
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(auth)
    };
    fetch('https://api-recipes.luanabuca.com/users', options)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        if(data.error===1){
          alert('One of the inputs is not correctly completed');
        }else{
          alert('You have successfuly created an account');
        }
      })
      .catch(error => console.error(error));
  }

  return (
    <div className='sign-up'>
      <h1 id='sign-up-title' >Sign Up</h1>

      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor="name">Name: </label>
        <input id='name' className='input-item' type="text" onChange={(e) => {setName(e.target.value)}} placeholder='Name' {...register('Name', {required:true, maxLength:80})}/>

        <label htmlFor="username">Username: </label>
        <input id='username' className='input-item' type="text" onChange={(e) => {setUsername(e.target.value)}} placeholder='Username' {...register('Username', {required:true, maxLength:80})} />

        <label htmlFor="password">Password: </label>
        <input id='password' className='input-item' type="password" onChange={(e) => {setPassword(e.target.value)}} placeholder='Password' {...register('Password', {required:true, maxLength:9})} />
        
        <p id='p-item'><small>The password requires a max of 8 characters.</small></p>

        <button id='create-btn' type='submit' onClick={handleCreateAcc}>Create an account</button>

        <p>
          <Link to='../sign-in'>Already have an account? Click here to sign in!</Link>
        </p>
      </form>
    </div>
  )
}
