import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './recipe-cat.css';


export default function RecipeCategory(){
  function handleCategoryclick(categoryId){
    window.location.replace('/category?category_id='+categoryId);
  }

  const [categories, setCategories] = useState([]);

  const options = {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
    }
  };
  fetch('https://api-recipes.luanabuca.com/categories/search', options)
    .then(response => response.json())
    .then(data => {
      setCategories(data['payload']['categories']);
    })
    .catch(error => console.log(error));

  return (
    
    <div className="recipe_category">
      <h3>Recipe Categories</h3>
        {categories.map(category => (
          <li key={category.id}><a href="#" onClick={() => handleCategoryclick(category.id)}>{category.name} ({category.recipe_count})</a></li>
        ))}
      </div>
  )
}
