import React, {useState, useEffect} from "react"; 

export default function EditForm(){
    if(localStorage.getItem('token')==null){
        window.location.replace('/sign-in');
    }

    return(
        <div className="edit-form">
            <h2 id="edit-title">Edit this recipe</h2>

            <div className="form">

                <label htmlFor="title">Recipe title</label>
                <input type="text" id="title" className="form-item" placeholder="Recipe title" />

                <label htmlFor="description">General description</label>
                <p className='parag'><small>Here you will have to type a few words about your recipe, it's ingredients, and steps of your delicious recipe.</small></p>
                <textarea id="description" className="form-item" ></textarea>

                <label htmlFor="img-link">Image link</label>
                <input type="url" id="img-link" className="form-item" placeholder="Image link" />

                <label htmlFor="category-type">Categpry</label>
                <select id="category-type" className="form-item">
                    {categories.map(category => (
                        <option key={category.id}>{category.name}</option>
                    ))}
                </select>

                <label htmlFor="complexity">Complexity</label>
                <select id="complexity" className="form-item">
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>

                <button id="edit-btn" type="submit">Edit Recipe</button>
            </div>
        </div>
    )
}
