import React, {useState, useEffect} from "react"; 
import './add-form.css';

export default function AddForm(){

  //check token
  if(localStorage.getItem('token')==null){
    window.location.replace('/sign-in');
  }

  //
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: '',
    category_id: '',
    complexity: '',
  });

  function handleChange(e) {
    const {title, value} = e.target;
    setFormData({...formData, [title]: value});
  };

  //submit
  const optionsPost = {
    method: 'POST',
    body: JSON.stringify(formData),
    headers: {
      'Content-Type': 'application/json',
      'jwt': localStorage.getItem('token')
    }
  };

  function handleSubmit(){
    alert('sunt aici');
    fetch('https://api-recipes.luanabuca.com/recipes', optionsPost)
      .then((response) => {
        console.log('Recipe created:', response.data);
      })
      .catch((error) => {
        console.error('Error creating recipe: ', error);
      });
  };

//get categories
  const [categories, setCategories] =useState([]);
    const optionsGet = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    };

    useEffect(() => {
      fetch('https://api-recipes.luanabuca.com/categories/search', optionsGet)
     .then(response => response.json())
     .then(data => {
      setCategories(data['payload']['categories']);
     })
     .catch(error => console.error(error));
    }, []);

  return (
    <div className='add-form'>
        <a href="/my-account">&larr; Go back to my account</a>
        <h2 id='form-title'>Add a new recipe</h2>

        <div className='form' onSubmit={handleSubmit}>
            {/* titlu */}
            <label htmlFor="title">Recipe title</label>
            <input id='title' className='form-item' type="text" placeholder='Recipe title'  onChange={handleChange}/>

            {/* descriere generala a retetei */}
            <label htmlFor="description">General description</label>
            <p className='parag'><small>Here you will have to type a few words about your recipe, it's ingredients, and steps of your delicious recipe.</small></p>
            <textarea id="description" className='form-item' onChange={handleChange}></textarea>

            {/* img link */}
            <label htmlFor="image">Image link</label>
            <input id='image' className='form-item' type="url" placeholder='Image link' onChange={handleChange}/>

            {/* category type */}
            <label htmlFor="category_id">Category</label>
            <select id="category_id" className='form-item' onChange={handleChange}>
              {categories.map(category => (
                <option key={category.id} value={category.id}>{category.name}</option>
              ))}
            </select>

            {/* complexity level */}
            <label htmlFor="complexity">Complexity</label>
            <select id='complexity' className='form-item' onChange={handleChange}>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
            </select>

            {/* button de add reteta */}
            <button id='add-btn' type='submit' onClick={handleSubmit}>Add Recipe</button>

        </div>
    </div>
  )
}
