import React, {useState, useEffect} from "react"; 
import './edit-form.css';

export default function EditForm(){
    //check token
    if(localStorage.getItem('token')==null){
        window.location.replace('/sign-in');
    }

    const [recipeTitle, setRecipeTitle] = useState('');
    const [recipeImage, setRecipeImage] = useState('');
    const [recipeDescription, setRecipeDescription] = useState("");
    const [recipeComplexity, setRecipeComplexity] = useState(0);
    const [recipeCategoryId, setRecipeCategoryId] = useState(0);

    const [recipeError, setRecipeError] = useState(''); 

    //get categories
    const [categories, setCategories] = useState([]);
    const optionsGet = {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        }
    };

    useEffect(() => {
        fetch('https://api-recipes.luanabuca.com/categories/search', optionsGet)
         .then(response => response.json())
         .then(data => {
            setCategories(data['payload']['categories']);
         })
         .catch(error => console.error(error));
    }, []);

    //fill inputs for editing
    const queryParameters = new URLSearchParams(window.location.search)
    const recipeId = queryParameters.get('recipeId')

    const recipeUrl = 'https://api-recipes.luanabuca.com/recipes/'+recipeId;

    const [recipe, setRecipe] = useState([]);

    const options = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'jwt' : localStorage.getItem('token')
      }
    };

    useEffect(() => {
      fetch(recipeUrl, options)
        .then(response => response.json())
        .then(data => {
          setRecipe(data['payload']['recipe']);
        //   setRecipeTitle(recipe.title);
        //   setRecipeImage(recipe.image);
        //   setRecipeDescription(recipe.description);
        //   setRecipeComplexity(recipe.complexity);
        //   setRecipeCategoryId(recipe.category.id);
      })
       .catch(error => console.error(error));
    }, [])

    function handleUpdate(){
        console.log(recipeTitle);
        console.log(recipeImage);
        console.log(recipeDescription);
        console.log(recipeComplexity);
        console.log(recipeCategoryId);
    }

    return(
        <div className="edit-form">
            <a href="/my-account">&larr; Go back to my account</a>
            <h2 id="edit-title">Edit this recipe</h2>

            <div className="form">

                <label htmlFor="title">Recipe title</label>
                <input type="text" id="title" className="form-item" placeholder="Recipe title" value={recipe.title} onChange={(e) => setRecipeTitle(e.target.value)}/>

                <label htmlFor="description">General description</label>
                <p className='parag'><small>Here you will have to type a few words about your recipe, it's ingredients, and steps of your delicious recipe.</small></p>
                <textarea id="description" className="form-item" defaultValue={recipe.description} onChange={(e) => setRecipeDescription(e.target.value)}></textarea>

                <label htmlFor="image">Image link</label>
                <input type="url" id="image" className="form-item" placeholder="Image link" value={recipe.image} onChange={(e) => setRecipeImage(e.target.value)}/>

                <label htmlFor="category_id">Category</label>
                {recipe.category && 
                <select id="category_id" className="form-item" defaultValue={recipe.category.id} onChange={(e) => setRecipeCategoryId(e.target.value)}>
                    {categories.map(category => (
                        <option key={category.id} value={category.id}>{category.name}</option>
                    ))}
                </select> 
                }

                <label htmlFor="complexity">Complexity</label>
                <select id="complexity" className="form-item" defaultValue={recipe.complexity} onChange={(e) => setRecipeComplexity(e.target.value)}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>

                <button id="edit-form-btn" type="submit" onClick={handleUpdate}>Update Recipe</button>
            </div>
        </div>
    )
}
