import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../UserContext';
import { useNavigate } from 'react-router-dom';
import './sign-in.css'

//find out cum sa fac sa tin minte in acel obiect si token si sa salvez toate astea in Useri
//de pus o functie pe butonu de submit ca sa intri in cont (sa se inregistreze tokenu)

export default function SignIn(){
  
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState(null);
  const {user, setUser} = useContext(UserContext);

  const navigate = useNavigate();

  function handleLogin(event){
    event.preventDefault();
    const auth = {
      username: username,
      password: password
    };
    
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(auth)
    };

    fetch('https://api-recipes.luanabuca.com/auth/login', options)
      .then(response => response.json())
      .then((response) => {
        localStorage.setItem('access_token', JSON.stringify(response));
        setUser(response);
        navigate('/my-account');
      });


      // .then(data => {
      //   console.log(data);
      //   if(data.error===1){
      //     alert ('Wrong username or password');
      //   }else{
      //     alert('You are in');
      //   }
      // })
      // .catch(error => console.error(error));
  }

  useEffect(() => {
    function validatePassword(password){
      const specialChar = ['*', '#', '@'];

      if(password <= 8){
        setPasswordError('Password must be at least 8 characters');
        return;
      }

      let hasSpecialChar = false;

      for(let char of password){
        const hasChar = specialChar.includes(char);
        if(hasChar){
          hasSpecialChar = true;
        }
      }

      if(!hasSpecialChar){
        setPasswordError('Your password must hasve at least one special character', specialChar)
      }else{
        setPasswordError(null);
      }
    }
    validatePassword(password);
  }, [password]);

  

  return (
    <div className='sign-in' onSubmit={handleLogin}>
      <h1 id='sign-in-title'>Sign In</h1>

      <div>
        <label htmlFor="username">Username: </label>
        <input type="text" id='username' className='input-item' onChange={(e) => {setUsername(e.target.value)}} placeholder='Username' />
      </div>

      <div>
        <label htmlFor="password">Password: </label>
        <input type="password" id='password' className='input-item' onChange={(e) => {setPassword(e.target.value)}} placeholder='Password' />
      </div>

      {passwordError && (
        <p>{passwordError}</p>
      )}

      <div>
        <input type="button" id='sign-in-btn' onClick={handleLogin} value={'Login'} />
      </div>

      <p>
        <Link to='../sign-up'>Don't have an account yet? Click here to create one!</Link>
      </p>
    </div>
  )
}
