import React from 'react';
import { useState, useEffect } from 'react';

export default function RecipePage() {

  const queryParameters = new URLSearchParams(window.location.search)
  const recipeId = queryParameters.get('recipe_id')

  // let recipeTitle = '';
  // let recipeURL = '';

  // if(recipeId=={recipe.id}){
  //   recipeTitle = {recipe.title}
  //   recipeURL = 'https://api-recipes.luanabuca.com/recipes/public/{id}&recipe_id='+recipeId;
  // }
  const [recipes, setRecipes] = useState([]);
  const options = {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
    }
  };

  useEffect(() => {
    fetch('https://api-recipes.luanabuca.com/recipes/public/{id}', options)
      .then(response => response.json())
      .then(data => {
        setRecipes(data['payload']['recipes']);
      })
      .catch(error => console.error(error));
  }, []);


  return (
    <div>
      {recipes.map(recipe => (
        <div className='recipe-page-container' key={recipe.id}>
          <h3>{recipe.title}</h3>
          <img src={recipe.image} />
          <p>{recipe.category}</p>
          <p>{recipe.complexity}</p>
          <p>{recipe.description}</p>
        </div>
      ))}
    </div>
  )
}
