import React, { useState } from 'react';
import {useForm} from 'react-hook-form';
import { Link } from 'react-router-dom';
import './sign-in.css'

//find out cum sa fac sa tin minte in acel obiect si token si sa salvez toate astea in Useri
//de pus o functie pe butonu de submit ca sa intri in cont (sa se inregistreze tokenu)

export default function SignIn(){
  const {register, handleSubmit, formState: {errors}} = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  function handleLogin(){
    const auth = {
      username: username,
      password: password
    };
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(auth)
    };
    fetch('https://api-recipes.luanabuca.com/auth/login', options)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        if(data.error===1){
          alert ('Wrong username or password');
        }else{
          alert('You are in');
        }
      })
      .catch(error => console.error(error));
  }

  return (
    <div className='sign-in'>
      <h1 id='sign-in-title'>Sign In</h1>

      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor="username">Username: </label>
        <input id='username' className='input-item' type="text" onChange={(e) => {setUsername(e.target.value)}} placeholder='Username' {...register('Username', {required:true, maxLength:80})} />

        <label htmlFor="password">Password: </label>
        <input id='password' className='input-item' type="password" onChange={(e) => {setPassword(e.target.value)}} placeholder='Password' {...register('Password', {required:true, maxLength:9})} />

        <button id='sign-in-btn' type='submit' onClick={handleLogin}>Sign In</button>

        <p>
          <Link to='../sign-up'>Don't have an account yet? Click here to create one!</Link>
        </p>
      </form>
    </div>
  )
}
