import React, {useState, useEffect} from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './my-account.css';
import {jwtDecode} from 'jwt-decode';


 export default function MyAccount() {

  if(localStorage.getItem('token')==null){
    window.location.replace('/sign-in');
  }
  const user = jwtDecode(localStorage.getItem('token'));

  let userType = '';
  if(user.type_id==1){
    userType = 'an administrator';
  }

  if(user.type_id==2){
    userType = 'a regular user';
  }

  function handleAddButton(){
    window.location.replace('/my-account/add-form');
  }

  const recipeUrl = 'https://api-recipes.luanabuca.com/recipes/search'
  const [recipes, setRecipes] = useState([]);

    const options = {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
            'jwt' : localStorage.getItem('token')
        }

    };

    useEffect(() => {
        fetch(recipeUrl, options)
         .then(response => response.json())
         .then(data => {
        setRecipes(data['payload']['recipes']);
      })
      .catch(error => console.log(error));
    }, []);

  return (

    <div className='my-account'>
      <h2>My Account</h2>
      <h3>Hello {user.name}</h3>
      <button onClick={handleAddButton}>Add recipe</button>
        <div>
          Here are the recipes you can manage as {userType}.
        </div>
        {recipes.map(recipe => (
            <div className='recipe-list-item' key={recipe.id}>
                <h4>{recipe.title}</h4>
                <a href={'#'} key={recipe.id} onClick={() => handleRecipeEdit(recipe.id)}>Edit</a>
                <a href={'#'} key={recipe.id} onClick={() => handleRecipeDelete(recipe.id)}>Delete</a>
            </div>
        ))}
    </div>
  )
}
