import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {useState} from 'react';
import './add-form.css';

export default function AddForm(props){

  const {onSubmit, recipe = {} } = props;

  function handleSubmit(event){
    event.preventDefault();
    const {title, description, image, category, complexity, steps, duration} = event.target;

    const updatedRecipe = {
      ...recipe,
      title: title.value,
      description: description.value,
      image: image.value,
      category: category.value,
      complexity: complexity.value,
      steps: steps.value,
      duration: duration.value, 
    };
    onSubmit(updatedRecipe);
    event.target.rest();
  }

  const [categories, setCategories] =useState([]);
    const options = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    };
    fetch('https://api-recipes.luanabuca.com/categories/search', options)
     .then(response => response.json())
     .then(data => {
      setCategories(data['payload']['categories']);
     })
     .catch(error => console.error(error));

  return (
    <div className='add-form'>
        <h2 id='form-title'>Add a new recipe</h2>

        <div className='form'>
            {/* titlu */}
            <label htmlFor="title">Recipe title</label>
            <input id='title' className='form-item' type="text" placeholder='Recipe title' />

            {/* descriere generala a retetei */}
            <label htmlFor="description">General description</label>
            <p className='parag'><small>Here you will have to type a few words about your recipe and it's ingredients.</small></p>
            <textarea id="description" className='form-item' ></textarea>

            {/* img link */}
            <label htmlFor="img-link">Image link</label>
            <input id='img-link' className='form-item' type="url" placeholder='Image link'/>

            {/* category type */}
            <label htmlFor="category-type">Category</label>
            <select id="category-type" className='form-item'>
              {categories.map(category => (
                <option key={category.id}>{category.name}</option>
              ))}
            </select>

            {/* complexity level */}
            <label htmlFor="complexity">Complexity</label>
            <select id='complexity' className='form-item' >
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
            </select>

              {/* steps  */}
              <label htmlFor="steps">Steps</label>
              <p className='parag'><small>Here you can type the steps it takes to make your delicious recipe.</small></p>
              <textarea id='steps' className='form-item' ></textarea>

              {/* total duration, dupa : pun const-ul care aduna toate inputurile de duration de mai sus*/}
              <label htmlFor="duration">Total duration: </label>
              <input id='duration' className='form-item' type="number" placeholder='duration (minutes)'/>


            {/* button de add reteta, functie care sa adauge reteta la lista de retetete */}
            <button id='add-btn' type='submit'>Add Recipe</button>
        </div>
    </div>
  )
}
